// src/components/home/Hero.jsx
import React from 'react';
import styled from 'styled-components';
import { StyledLinkButton } from '../Button';

// --- INICIO DE LA SOLUCIÓN BASE64 ---

// 1. La imagen completa, codificada como texto.
const heroImageBase64 = "data:image/jpeg;base64,"

const HeroWrapper = styled.section`
  color: white;
  height: 60vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  
  /* 2. Usamos la variable Base64 directamente como fondo */
  background-image: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url("${heroImageBase64}");
  
  background-size: cover;
  background-position: center;
`;


const HeroContent = styled.div`
  max-width: 800px;
  h1 {
    font-size: 3.5rem;
    font-weight: 900;
    text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.7);
  }
  p {
    font-size: 1.25rem;
    margin-bottom: 2rem;
    color: var(--color-text-muted);
  }
`;

function Hero() {
  return (
    <HeroWrapper>
      <HeroContent>
        <h1 className="display-3">Tecnología que Inspira tu Futuro</h1>
        <p className="lead">
          Encuentra los gadgets, componentes y accesorios de última generación.
          Calidad y rendimiento garantizados.
        </p>
        <StyledLinkButton
          to="/productos"
          $variant="primary"
          style={{ fontSize: "1.2rem", padding: "15px 30px" }}
        >
          Explorar Productos
        </StyledLinkButton>
      </HeroContent>
    </HeroWrapper>
  );
}

export default Hero;
